from flask import Flask, render_template, requestfrom werkzeug.utils import secure_filenamefrom skimage import iofrom keras.models import load_modelimport cv2from PIL import Image #use PILimport numpy as npapp = Flask(__name__)@app.route('/', methods=['GET', 'POST'])def upload_file():    if request.method == 'POST':        file = request.files['file']        print("File Received")        filename = secure_filename(file.filename)        print(filename)        dict = ['NORMAL','PNEUMONIA']        file.save("./static/"+filename) #Heroku no need static        file = open("./static/"+filename,"r") #Heroku no need static        model = load_model("Pneumonia")        image = cv2.imread("./static/"+filename,cv2.IMREAD_COLOR)        image = cv2.resize(image,(100,100))        image = image.astype('float64')/255        image = image.reshape(1, image.shape[0], image.shape[1], image.shape[2])        pred= dict[model.predict(image).argmax()]        return(render_template("index.html", result=str(pred)))    else:        return(render_template("index.html", result="WAITING"))if __name__ == "__main__":    app.run()